import React, { useState } from 'react';
import axios from 'axios';
import Style from './updateGoal.module.scss';

import { ListAllGoals } from 'components';

export default function UpdateGoal(props) {
	const [nameInput, setNameInput] = useState(props.name);
	const [categoryInput, setCategoryInput] = useState(props.category);
	const [customCategoryInput, setCustomCategoryInput] = useState('');
	const [customCategory, setCustomCategory] = useState(false);

	const [visible, setVisible] = useState(true);

	async function update(e) {
		e.preventDefault();
		let data = {
			name: nameInput,
			category: categoryInput,
		};
		try {
			const goals = await axios({
				method: 'put',
				url: `http://localhost:10000/goals/${props.id}`,
				data: data,
			});
			console.log(`GOAL UPDATED`);
			setVisible(false);
			// setNameInput('');
			// setCategoryInput('--Select--');
			// setCustomCategory(false);
			// setCustomCategoryInput('');
		} catch (e) {
			console.log(e);
		}
	}

	function onCategoryChange(e) {
		setCategoryInput(e.target.value);
		e.target.value === 'Custom' ? setCustomCategory(true) : setCustomCategory(false);
		console.log(e.target.value);
	}

	return;
	{
		visible === true ? (
			<div className={Style.modalBackground}>
				<div className={Style.modal}>
					<h4>Update Goal</h4>
					<form className={Style.form}>
						<div>
							<label htmlFor='goal'>Goal</label>
							<input
								type='text'
								name='goal'
								id='goal'
								value={nameInput}
								onChange={(e) => setNameInput(e.target.value)}
							/>
						</div>
						<div>
							<label htmlFor='category'>Category</label>
							<select name='category' id='category' value={categoryInput} onChange={onCategoryChange}>
								<option value=''>--Select--</option>
								<option value='Fitness'>Fitness</option>
								<option value='Nutrition'>Nutrition</option>
								<option value='Mindfulness'>Mindfulness</option>
								<option value='Custom'>Custom</option>
							</select>
						</div>
						{customCategory === true ? (
							<div className={Style.customCategory}>
								<input
									type='text'
									name='category'
									id='category'
									placeholder='Enter Custom Category'
									value={customCategoryInput}
									onChange={(e) => setCustomCategoryInput(e.target.value)}
								/>
							</div>
						) : (
							''
						)}
						<button onClick={update}>Submit</button>
					</form>
				</div>
			</div>
		) : (
			<div></div>
		);
	}
}
