import React, { useState } from 'react';
import axios from 'axios';
import Style from './editGoals.module.scss';

import { ListAllGoals } from 'components';

export default function EditGoals() {
	// for goalsList, create goals display list and transfer data (including id's)
	// for goals that are multiplied, create an array of the id's and push into it, along with adding to the x/s per week count++
	// to delete multiple, slice the array and run delete request for each array

	const [nameInput, setNameInput] = useState('');
	const [categoryInput, setCategoryInput] = useState('');
	const [customCategoryInput, setCustomCategoryInput] = useState('');
	const [timesPerWeek, setTimesPerWeek] = useState(0);

	const [customCategory, setCustomCategory] = useState(false);

	async function updateGoal(e) {
		e.preventDefault();
		try {
			const goals = await axios({
				method: 'put',
				url: 'http://localhost:10000/goals/62dc7a3dae41d256ec5de1c3',
				data: {
					category: 'Whatever',
				},
			});
			console.log(`GOAL UPDATED`);
			setNameInput('');
			setCategoryInput('--Select--');
			setTimesPerWeek(0);
			setCustomCategory(false);
			setCustomCategoryInput('');
		} catch (e) {
			console.log(e);
		}
	}

	function onCategoryChange(e) {
		setCategoryInput(e.target.value);
		e.target.value === 'Custom' ? setCustomCategory(true) : setCustomCategory(false);
		console.log(e.target.value);
	}

	return (
		<div>
			<div className={Style.container}>
				<h4>Update Goal</h4>
				<form className={Style.form}>
					<div>
						<label htmlFor='goal'>Goal</label>
						<input
							type='text'
							name='goal'
							id='goal'
							value={nameInput}
							onChange={(e) => setNameInput(e.target.value)}
						/>
					</div>
					<div>
						<label htmlFor='category'>Category</label>
						<select name='category' id='category' value={categoryInput} onChange={onCategoryChange}>
							<option value=''>--Select--</option>
							<option value='Fitness'>Fitness</option>
							<option value='Nutrition'>Nutrition</option>
							<option value='Mindfulness'>Mindfulness</option>
							<option value='Custom'>Custom</option>
						</select>
					</div>
					{customCategory === true ? (
						<div className={Style.customCategory}>
							<input
								type='text'
								name='category'
								id='category'
								placeholder='Enter Custom Category'
								value={customCategoryInput}
								onChange={(e) => setCustomCategoryInput(e.target.value)}
							/>
						</div>
					) : (
						''
					)}

					<div>
						<label htmlFor='times'>Times per week</label>
						<input
							type='number'
							id='times'
							value={timesPerWeek}
							onChange={(e) => setTimesPerWeek(e.target.value)}
						/>
					</div>

					<button onClick={updateGoal}>Submit</button>
				</form>
			</div>
			<ListAllGoals />
		</div>
	);
}
