import React, { useState } from 'react';
import axios from 'axios';
import Style from './schedule.module.scss';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

import { GoalCalendarBlock } from 'components';

export default function Schedule() {
	const [allCategories, setAllCategories] = useState([]);
	const [goalDisplayBlocks, setGoalDisplayBlocks] = useState([]);
	const [goalFitnessBlocks, setGoalFitnessBlocks] = useState([]);
	const [goalNutritionBlocks, setGoalNutritionBlocks] = useState([]);

	async function getGoals() {
		try {
			const goals = await axios.get('http://localhost:10000/goals');
			const goalsList = goals.data;
			console.log(goals.data);

			let allGoals = [];
			for (const goal in goalsList) {
				const count = goalsList[goal].timesPerWeek;
				for (let i = 0; i < count; i++) {
					const dropId = goalsList[goal]._id.concat(`_${i}`);
					const data = { ...goalsList[goal], dropId };
					allGoals.push(data);
				}
			}
			setGoalFitnessBlocks(allGoals.filter((goal) => (goal.category = 'Fitness')));
			setGoalNutritionBlocks(allGoals.filter((goal) => (goal.category = 'Nutrition')));
			setGoalDisplayBlocks(allGoals);
		} catch (e) {
			console.log(e);
		}
	}

	async function getCategories() {
		try {
			const categories = await axios.get('http://localhost:10000/category');
			setAllCategories(categories.data);
		} catch (e) {
			console.log(e);
		}
	}

	// this will run the axios request on the first render of the component
	let needToLoad = goalDisplayBlocks.length === 0 && allCategories.length === 0;
	if (needToLoad) {
		getGoals();
		getCategories();
	}

	/*this will be used to make sure that all of the data has been loaded
	before returning the page content... returning the child components without the data causes an error*/
	let loaded = goalDisplayBlocks.length !== 0 && allCategories.length !== 0;

	function handleOnDragEnd(result) {
		if (!result.destination) return;
		console.log(result);
		const items = Array.from(goalDisplayBlocks);
		const [reorderedItem] = items.splice(result.source.index, 1);
		items.splice(result.destination.index, 0, reorderedItem);

		setGoalDisplayBlocks(items);
	}

	const weekdays = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];

	return (
		<div className={Style.page}>
			{/* {loaded && (
				<DragDropContext onDragEnd={handleOnDragEnd}>
					<Droppable droppableId='goals'>
						{(provided) => (
							<ul className='goals' {...provided.droppableProps} ref={provided.innerRef}>
								{goalDisplayBlocks.map((goal, index) => (
									<Draggable key={goal.dropId} draggableId={goal.dropId} index={index}>
										{(provided) => (
											<li
												ref={provided.innerRef}
												{...provided.draggableProps}
												{...provided.dragHandleProps}>
												<GoalCalendarBlock
													goal={goal}
													category={allCategories.filter((cat) => cat.name === goal.category)}
												/>
											</li>
										)}
									</Draggable>
								))}
								{provided.placeholder}
							</ul>
						)}
					</Droppable>
				</DragDropContext>
			)}
			{loaded && (
				<ul>
					{weekdays.map((day, index) => (
						<ul key={index}>{day}</ul>
					))}
				</ul>
			)} */}
			{loaded && (
				<DragDropContext onDragEnd={handleOnDragEnd}>
					<Droppable droppableId='goals'>
						{(provided) => (
							<ul className='goals' {...provided.droppableProps} ref={provided.innerRef}>
								{goalDisplayBlocks.map((goal, index) => (
									<Draggable key={goal.dropId} draggableId={goal.dropId} index={index}>
										{(provided) => (
											<li
												ref={provided.innerRef}
												{...provided.draggableProps}
												{...provided.dragHandleProps}>
												<GoalCalendarBlock
													goal={goal}
													category={allCategories.filter((cat) => cat.name === goal.category)}
												/>
											</li>
										)}
									</Draggable>
								))}
								{provided.placeholder}
							</ul>
						)}
					</Droppable>
				</DragDropContext>
			)}
			{loaded && (
				<DragDropContext onDragEnd={handleOnDragEnd}>
					<Droppable droppableId='goals'>
						{(provided) => (
							<ul className='goals' {...provided.droppableProps} ref={provided.innerRef}>
								{goalDisplayBlocks.map((goal, index) => (
									<Draggable key={goal.dropId} draggableId={goal.dropId} index={index}>
										{(provided) => (
											<li
												ref={provided.innerRef}
												{...provided.draggableProps}
												{...provided.dragHandleProps}>
												<GoalCalendarBlock
													goal={goal}
													category={allCategories.filter((cat) => cat.name === goal.category)}
												/>
											</li>
										)}
									</Draggable>
								))}
								{provided.placeholder}
							</ul>
						)}
					</Droppable>
				</DragDropContext>
			)}
		</div>
	);
}
