import React, { useState } from 'react';
import axios from 'axios';
import Style from './deleteGoal.module.scss';

export default function DeleteGoal(props) {
	async function update(e) {
		e.preventDefault();
		const id = props.id;
		try {
			const goals = await axios({
				method: 'put',
				url: `http://localhost:10000/goals/${props.id}`,
			});
			console.log(`GOAL UPDATED`);
			// setNameInput('');
			// setCategoryInput('--Select--');
			// setCustomCategory(false);
			// setCustomCategoryInput('');
		} catch (e) {
			console.log(e);
		}
	}

	return (
		<div className={Style.modalBackground}>
			<div className={Style.modal}>
				<h4>Are you sure?</h4>
				<div className={Style.exitButton} onClick={props.unmount}>
					X
				</div>
			</div>
		</div>
	);
}
