import React from 'react';
// import Style from './editGoals.module.scss';
// import axios from 'axios';

class EditGoals extends React.Component {
	constructor(props) {
		super(props);
		this.state = { apiResponse: '' };
	}
	render() {
		return (
			<div>
				<div className={Style.AddNewContainer}>
					<h4>Add New Goal</h4>
					<form className={Style.form}>
						<div>
							<label htmlFor='goal'>Goal</label>
							<input type='text' name='goal' id='goal' ref={nameInput} />
						</div>
						<div>
							<label htmlFor='category'>Category</label>
							<select name='category' id='category' ref={categoryInput}>
								<option value=''>--Select--</option>
								<option value='Fitness'>Fitness</option>
								<option value='Nutrition'>Nutrition</option>
								<option value='Mindfulness'>Mindfulness</option>
							</select>
						</div>
						<div>
							<label htmlFor='times'>Times per week</label>
							<input type='number' id='times' ref={timesPerWeek} />
						</div>
						<button onClick={addGoal} className={Style.smallButton}>
							Submit
						</button>
					</form>
				</div>
				<div className={Style.ListContainer}>
					{/* <button onClick={getAllGoals}>Get Goals List</button> */}
					<h2>{props.category}</h2>
					<ul>
						{goalsList.map((goal, index) => (
							<li key={index}> {goal.name} </li>
						))}
					</ul>
				</div>
			</div>
		);
	}
}

export default EditGoals;
