import React, { useState, useRef } from 'react';
import axios from 'axios';
import Style from './circularProgressBar.module.scss';

export default function CircularProgressBar(props) {
	// const [progressValue, setProgressValue] = useState(0);
	const progressValue = useRef(0);
	const circularProgress = useRef(0);

	let progressStartValue = 0;
	let progressEndValue = 90;
	let speed = 30;

	let progress = setInterval(() => {
		progressStartValue++;
		progressValue.current.textContent = `${progressStartValue}%`;
		circularProgress.current.style.background = `conic-gradient(${progressStartValue * 3.6}deg, #ededed 0deg)`;

		if (progressStartValue === progressEndValue) {
			clearInterval(progress);
		}
		// console.log(progressStartValue);
	}, speed);

	return (
		<div>
			<div className={Style.progressContainer}>
				<div className={Style.circularProgress} ref={circularProgress}>
					<span className={Style.progressValue} ref={progressValue}>
						0%
					</span>
				</div>

				<span className={Style.text}>{props.category.name}</span>
			</div>
		</div>
	);
}
