import React, { useState } from 'react';
import axios from 'axios';
import Style from './colorSelect.module.scss';
import { FormControl, TextField, Select, InputLabel, MenuItem, Button } from '@mui/material';
import { ThemeProvider } from '@mui/material/styles';
import theme from '../../../theme.js';

export default function ColorSelect(props) {
	const [categoryInput, setCategoryInput] = useState('');

	return (
		<div className={Style.colorSelect}>
			<h2 className={Style.header}>Select Category Colors</h2>
			<div className={Style.colorList}>
				<div className={Style.colorOption1}></div>
				<div className={Style.colorOption2}></div>
				<div className={Style.colorOption3}></div>
				<div className={Style.colorOption4}></div>
				<div className={Style.colorOption5}></div>
				<div className={Style.colorOption6}></div>
				<div className={Style.colorOption7}></div>
				<div className={Style.colorOption8}></div>
			</div>
			<ThemeProvider theme={theme}>
				<FormControl fullWidth margin='dense'>
					<InputLabel id='category'>Select Category</InputLabel>
					<Select
						color='secondary'
						labelId='category'
						id='category'
						variant='outlined'
						label='Select a Category'
						value={categoryInput}
						onChange={(e) => setCategoryInput(e.target.value)}>
						{props.categories.map((option) => (
							<MenuItem key={option} value={option}>
								{option}
							</MenuItem>
						))}
					</Select>
				</FormControl>
				<Button variant='outlined' fullWidth>
					Set
				</Button>
			</ThemeProvider>
		</div>
	);
}
