import React, { useState } from 'react';
import axios from 'axios';
import Style from './listAllGoals.module.scss';

import { AddGoal } from 'components';

export default function ListAllGoals() {
	const [goalsList, setGoalsList] = useState([]);

	async function getGoals() {
		try {
			const goals = await axios.get('http://localhost:10000/goals');
			if (JSON.stringify(goalsList) !== JSON.stringify(goals.data)) {
				setGoalsList(goals.data);
				console.log(goals.data);
			}
		} catch (e) {
			console.log(e);
		}
	}

	// this will run the axios request on the first render of the ListAllGoals component
	// getGoals function will be passed through as prop (rerenderList) for AddGoal component
	// --> so that after adding a new goal, getGoals can be called to update the list and rerender
	if (goalsList.length === 0) {
		getGoals();
	}

	let categories = [];
	for (const goal in goalsList) {
		const category = goalsList[goal].category;
		let catExists = false;
		for (const cat in categories) {
			if (category.toLowerCase() === categories[cat].toLowerCase()) {
				catExists = true;
				break;
			}
		}
		if (!catExists) {
			categories.push(category);
		}
	}

	// for (const goal in goalsList) {
	// 	const goalData = goalsList[goal];
	// 	const goalName = goalsList[goal].name;
	// 	const category = goalsList[goal].category;
	// 	let catExists = false;
	// 	for (const cat in categories) {
	// 		if (category.toLowerCase() === categories[cat].toLowerCase()) {
	// 			catExists = true;
	// 		}
	// 	}
	// 	if (!catExists) {
	// 		const li = { ...goalData, count: 1 };
	// 		categories.push(category);
	// 		allGoals.push(li);
	// 	} else {
	// 		let goalExists = false;
	// 		for (const obj in allGoals) {
	// 			if (goalName === allGoals[obj].name) {
	// 				goalExists = true;
	// 				allGoals[obj].count++;
	// 			}
	// 		}
	// 		if (!goalExists) {
	// 			const li = { ...goalData, count: 1 };
	// 			allGoals.push(li);
	// 		}
	// 	}
	// }

	return (
		<div>
			<AddGoal rerenderList={getGoals} />
			<div>
				{categories.map((cat, index) => (
					<div
						key={index}
						className={
							cat === 'Fitness'
								? Style.fitness
								: cat === 'Nutrition'
								? Style.nutrition
								: cat === 'Mindfulness'
								? Style.mindfulness
								: Style.container
						}>
						<div>
							<h2>{cat}</h2>
							<ul>
								{goalsList
									.filter((goal) => goal.category.toLowerCase() === cat.toLowerCase())
									.map((goal, index) => (
										<li key={index}>
											{goal.name}
											<span className={Style.listCount}>{goal.timesPerWeek}</span>
										</li>
									))}
							</ul>
						</div>
					</div>
				))}
			</div>
		</div>
	);
}
