import React from 'react';
import Style from './editGoals.module.scss';
import axios from 'axios';

import { ListAllGoals } from 'components';

class EditGoals extends React.Component {
	constructor(props) {
		super(props);
		this.state = { nameImput: '', categoryInput: '', timesPerWeek: 0 };
	}

	handleNameInputChanged(e) {
		setNameInput(e.target.value);
		console.log(nameInput);
	}

	addGoal() {
		console.log('ADDING');
	}

	render() {
		return (
			<div>
				<div className={Style.Container}>
					<h4>Add New Goal</h4>
					<form className={Style.form}>
						<div>
							<label htmlFor='goal'>Goal</label>
							<input
								type='text'
								name='goal'
								id='goal'
								value={this.state.nameInput}
								onChange={this.handleNameInputChanged.bind(this)}
							/>
						</div>
						<div>
							<label htmlFor='category'>Category</label>
							<select name='category' id='category'>
								<option value=''>--Select--</option>
								<option value='Fitness'>Fitness</option>
								<option value='Nutrition'>Nutrition</option>
								<option value='Mindfulness'>Mindfulness</option>
							</select>
						</div>
						<div>
							<label htmlFor='times'>Times per week</label>
							<input type='number' id='times' />
						</div>
						<button onClick={this.addGoal} className={Style.smallButton}>
							Submit
						</button>
					</form>
				</div>
				<ListAllGoals category='Fitness' />
				<ListAllGoals category='Nutrition' />
				<ListAllGoals category='Mindfulness' />
			</div>
		);
	}
}

export default EditGoals;
