import React, { useState } from 'react';
import axios from 'axios';
import Style from './schedule.module.scss';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

import { GoalCalendarBlock } from 'components';

export default function Schedule() {
	const [allCategories, setAllCategories] = useState([]);
	const [unassignedGoals, setUnassignedGoals] = useState([]);
	const [mondayGoals, setMondayGoals] = useState([]);
	const [tuesdayGoals, setTuesdayGoals] = useState([]);
	const [wednesdayGoals, setWednesdayGoals] = useState([]);
	const [thursdayGoals, setThursdayGoals] = useState([]);
	const [fridayGoals, setFridayGoals] = useState([]);
	const [saturdayGoals, setSaturdayGoals] = useState([]);
	const [sundayGoals, setSundayGoals] = useState([]);

	let goalsList;

	async function getGoals() {
		try {
			const goals = await axios.get('http://localhost:10000/goals');
			goalsList = goals.data;
			console.log(goals.data);

			let allGoals = [];
			for (const goal in goalsList) {
				const count = goalsList[goal].timesPerWeek;
				for (let i = 0; i < count; i++) {
					const dropId = goalsList[goal]._id.concat(`_${i}`);
					//create currentDay
					const dayInfo = goalsList[goal].days[i];
					const data = { ...goalsList[goal], dropId, dayInfo };
					allGoals.push(data);
				}
			}
			setUnassignedGoals(allGoals.filter((goal) => 'unassigned' in goal.dayInfo));
			setMondayGoals(allGoals.filter((goal) => 'monday' in goal.dayInfo));
			setTuesdayGoals(allGoals.filter((goal) => 'tuesday' in goal.dayInfo));
			setWednesdayGoals(allGoals.filter((goal) => 'wednesday' in goal.dayInfo));
			setThursdayGoals(allGoals.filter((goal) => 'thursday' in goal.dayInfo));
			setFridayGoals(allGoals.filter((goal) => 'friday' in goal.dayInfo));
			setSaturdayGoals(allGoals.filter((goal) => 'saturday' in goal.dayInfo));
			setSundayGoals(allGoals.filter((goal) => 'sunday' in goal.dayInfo));
		} catch (e) {
			console.log(e);
		}
	}

	async function getCategories() {
		try {
			const categories = await axios.get('http://localhost:10000/category');
			setAllCategories(categories.data);
		} catch (e) {
			console.log(e);
		}
	}

	// this will run the axios request on the first render of the component
	let needToLoad =
		allCategories.length === 0 &&
		unassignedGoals.length === 0 &&
		mondayGoals.length === 0 &&
		tuesdayGoals.length === 0 &&
		wednesdayGoals.length === 0 &&
		thursdayGoals.length === 0 &&
		fridayGoals.length === 0 &&
		saturdayGoals.length === 0 &&
		sundayGoals.length === 0;

	if (needToLoad) {
		getGoals();
		getCategories();
	}

	/*this will be used to make sure that all of the data has been loaded
	before returning the page content... returning the child components without the data causes an error*/
	let loaded = allCategories.length !== 0 && unassignedGoals.length !== 0;

	function handleOnDragEnd(result) {
		if (!result.destination) return;
		console.log(result);

		/*
		> splice the reordered goal from the original list
		-----
		> save goal.days to variable
		> if not coming from 'unassigned' -> delete property for the day it came from
		> add new property with key as the day and value as the dayIndex
			ex: { tuesday: 2 }
		>update goal.days in the reordered goal to reflect the changes
		-----
		> splice the destination list and insert the modified goal

		*/

		// const items = Array.from(goalDisplayBlocks);
		// const [reorderedItem] = items.splice(result.source.index, 1);
		// items.splice(result.destination.index, 0, reorderedItem);
		// console.log(items);

		// setGoalDisplayBlocks(items);
	}

	return (
		<div className={Style.page}>
			{loaded && (
				<DragDropContext onDragEnd={handleOnDragEnd}>
					<section className={Style.unassignedGoals}>
						<Droppable droppableId='unassigned'>
							{(provided) => (
								<ul className='unassigned' {...provided.droppableProps} ref={provided.innerRef}>
									GOALS
									{unassignedGoals.map((goal, index) => (
										<Draggable key={goal.dropId} draggableId={goal.dropId} index={index}>
											{(provided) => (
												<li
													ref={provided.innerRef}
													{...provided.draggableProps}
													{...provided.dragHandleProps}>
													<GoalCalendarBlock
														goal={goal}
														category={allCategories.filter(
															(cat) => cat.name === goal.category
														)}
													/>
												</li>
											)}
										</Draggable>
									))}
									{provided.placeholder}
								</ul>
							)}
						</Droppable>
					</section>
					<section className={Style.schedule}>
						<div className={Style.day}>
							<Droppable droppableId='monday'>
								{(provided) => (
									<ul className='monday' {...provided.droppableProps} ref={provided.innerRef}>
										MONDAY
										{mondayGoals.map((goal, index) => (
											<Draggable key={goal.dropId} draggableId={goal.dropId} index={index}>
												{(provided) => (
													<li
														ref={provided.innerRef}
														{...provided.draggableProps}
														{...provided.dragHandleProps}>
														<GoalCalendarBlock
															goal={goal}
															category={allCategories.filter(
																(cat) => cat.name === goal.category
															)}
														/>
													</li>
												)}
											</Draggable>
										))}
										{provided.placeholder}
									</ul>
								)}
							</Droppable>
						</div>
						<div className={Style.day}>
							<Droppable droppableId='tuesday'>
								{(provided) => (
									<ul className='tuesday' {...provided.droppableProps} ref={provided.innerRef}>
										TUESDAY
										{tuesdayGoals.map((goal, index) => (
											<Draggable key={goal.dropId} draggableId={goal.dropId} index={index}>
												{(provided) => (
													<li
														ref={provided.innerRef}
														{...provided.draggableProps}
														{...provided.dragHandleProps}>
														<GoalCalendarBlock
															goal={goal}
															category={allCategories.filter(
																(cat) => cat.name === goal.category
															)}
														/>
													</li>
												)}
											</Draggable>
										))}
										{provided.placeholder}
									</ul>
								)}
							</Droppable>
						</div>
						<div className={Style.day}>
